import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ClipLoader } from "react-spinners";

function Search() {
    const [query, setQuery] = useState('');
    const [rarity, setRarity] = useState('');
    const [results, setResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const [sortColumn, setSortColumn] = useState(null); // Column to sort by
    const [sortOrder, setSortOrder] = useState('asc'); // Sort order: 'asc' or 'desc'
    const [visibleItems, setVisibleItems] = useState(100);

    // Handle the form submission to trigger the search
    const handleSearch = async (event) => {
        if (event) event.preventDefault();  // Prevent form submission on search trigger
        setLoading(true);
        setError(null); // Clear previous errors

        try {
            // Make a request to the Flask API to search for items
            const response = await axios.get('http://localhost:5000/search', {
                params: { query, rarity },
            });
            setResults(response.data); // Update the state with the results
            setVisibleItems(100);
        } catch (error) {
            console.error('There was an error!', error);
            setError('There was an error with your search. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    // Load data when the component is mounted or when `query` or `rarity` changes
    useEffect(() => {
        handleSearch();  // Call search when the component loads (on page load)
    }, []);  // Empty dependency array means this runs once when the component mounts

    // Handle sorting
    const handleSort = (column) => {
        if (sortColumn === column) {
            // Toggle sort order if the same column is clicked
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
        } else {
            // Sort by a new column
            setSortColumn(column);
            setSortOrder('asc');
        }
    };

    // Apply sorting to the results
    const sortedResults = [...results].map((itemObj) => {
        // Flatten nested `results` arrays if they exist
        if (Array.isArray(itemObj.results)) {
            return itemObj.results.map((item) => ({
                ...item, // Include existing properties
                parentData: itemObj,
                uniqueKey: `${item.id || item.itemName || 'unknown'}-${parentIndex}-${childIndex}`,
            }));
        }
        return {
            ...itemObj,
            uniqueKey: `${itemObj.id || itemObj.itemName || 'unknown'}-${parentIndex}`,
        };
    }).flat().sort((a, b) => {
        if (!sortColumn) return 0; // Do nothing if no sort column is set

        const valA = a[sortColumn] || ''; // Handle missing values
        const valB = b[sortColumn] || '';

        // Handle numbers and strings differently for sorting
        if (typeof valA === 'number' && typeof valB === 'number') {
            return sortOrder === 'asc' ? valA - valB : valB - valA;
        }
        return sortOrder === 'asc' ? valA.localeCompare(valB) : valB.localeCompare(valA);
    });

    // Load more items
    const loadMore = () => {
        setVisibleItems((prev) => prev + 100);
    };

    return (
        <div>
            <h1>Search for Items</h1>
            <form onSubmit={handleSearch}>
                <div>
                    <input
                        type="text"
                        placeholder="Search for an item by name..."
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        style={{ width: '500px' }}
                        onKeyDownCapture={(e) => {
                            if (e.key === 'Enter') {
                                e.preventDefault();  // Prevent the default behavior
                                handleSearch(e);  // Manually trigger search
                            }
                        }}
                    />
                    <button type="submit">Search</button>
                </div>
            </form>

            {loading && <ClipLoader size={50} color="#36d7b7" />}

            {error && <p style={{ color: 'red' }}>{error}</p>}

            <h2>Results</h2>
            <table>
                <thead>
                    <tr>
                        <th onClick={() => handleSort('itemName')}>Item Name</th>
                        <th onClick={() => handleSort('rarity')}>Rarity</th>
                        <th onClick={() => handleSort('chance')}>Chance</th>
                        <th onClick={() => handleSort('source')}>Source</th>
                        <th onClick={() => handleSort('rotation')}>Rotation</th>
                        <th onClick={() => handleSort('stage')}>Stage</th>
                    </tr>
                </thead>
                <tbody>
                    {sortedResults.slice(0, visibleItems).map((item, index) => {
                        const uniqueKey = `${item.id || item.itemName || 'unknown'}-${index}`; // Combine fields to ensure uniqueness
                        return (
                            <tr key={uniqueKey}>
                                <td>{item.itemName}</td>
                                <td>{item.rarity || 'N/A'}</td>
                                <td style={{ minWidth: '50px' }}>{`${item.enemyModDropChance || item.chance} %`}</td>
                                <td>
                                    {item.tablename === "sortieRewards" ? <span>Sortie</span> :
                                        item.tablename === "transientRewards" ? item.objectiveName :
                                            item.objectiveName || item.source || item.enemyName ? (
                                                <a href={`https://warframe.fandom.com/wiki/${item.objectiveName || item.source || item.enemyName}`} target="_blank" rel="noopener noreferrer">
                                                    {item.objectiveName || item.source || item.enemyName}
                                                </a>
                                            ) : item.gameMode ? (
                                                <span>
                                                    ({item.planet}) {item.location}:
                                                    <br />
                                                    <a href={`https://warframe.fandom.com/wiki/${item.gameMode}`} target="_blank" rel="noopener noreferrer">
                                                        {item.gameMode}
                                                    </a>
                                                </span>
                                            ) : item.relicName ? (
                                                <span>
                                                    <a href={`https://warframe.fandom.com/wiki/${item.relicTier}_${item.relicName}`} target="_blank" rel="noopener noreferrer">
                                                        {item.relicTier} {item.relicName}
                                                    </a>
                                                    <span> </span>({item.relicState} {({
                                                        "Intact": "(0 traces)",
                                                        "Exceptional": "(25 traces)",
                                                        "Flawless": "(50 traces)",
                                                        "Radiant": "(100 traces)"
                                                    })[item.relicState] || "Unknown"})
                                                </span>
                                            ) : (
                                                item.keyName ||
                                                item.bountyLevel ||
                                                `${item.syndicateName} (${item.standing} standing)` ||
                                                '-'
                                            )
                                    }
                                </td>
                                <td>{item.rotation ? `Rotation ${item.rotation}` : '-'}</td>
                                <td>{item.stage ? `${item.stage}` : '-'}</td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>
            {visibleItems < results.length && (
                <button onClick={loadMore}>Load More</button>
            )}
        </div>
    );
}

export default Search;
