import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ClipLoader } from "react-spinners";

function Search() {
    const [query, setQuery] = useState('');
    const [rarity, setRarity] = useState('');
    const [results, setResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [offset, setOffset] = useState(0); // Offset for pagination
    const [hasMore, setHasMore] = useState(true); // Flag to check if more results exist
    const limit = 0; // Number of results to load at a time

    // Fetch results from the API
    const fetchResults = async () => {
        setLoading(true);
        setError(null);

        try {
            const response = await axios.get('http://localhost:5000/search', {
                params: { query, rarity, limit, offset },
            });
            const newResults = response.data;

            // Check if there are more results to load
            if (newResults.length < limit) {
                setHasMore(false); // No more results to load
            }

            // Append new results to existing ones
            setResults((prevResults) => [...prevResults, ...newResults]);
        } catch (error) {
            console.error('There was an error!', error);
            setError('There was an error with your search. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    // Trigger initial fetch on component mount
    useEffect(() => {
        fetchResults();
    }, []); // Empty dependency array ensures it only runs on mount

    // Load more results
    const loadMore = () => {
        setOffset((prevOffset) => prevOffset + limit); // Update offset for next set of results
    };

    // Trigger fetch when offset changes
    useEffect(() => {
        if (offset > 0) fetchResults();
    }, [offset]);

    return (
        <div>
            <h1>Search for Items</h1>
            <form
                onSubmit={(e) => {
                    e.preventDefault();
                    setResults([]); // Clear current results
                    setOffset(0); // Reset offset
                    setHasMore(true); // Reset `hasMore` flag
                    fetchResults(); // Fetch initial results
                }}
            >
                <div>
                    <input
                        type="text"
                        placeholder="Search for an item by name..."
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        style={{ width: '500px' }}
                    />
                    <button type="submit">Search</button>
                </div>
            </form>

            {loading && <ClipLoader size={50} color="#36d7b7" />}

            {error && <p style={{ color: 'red' }}>{error}</p>}

            <h2>Results</h2>
            <table>
                <thead>
                    <tr>
                        <th>Item Name</th>
                        <th>Rarity</th>
                        <th>Chance</th>
                        <th>Source</th>
                        <th>Rotation</th>
                        <th>Stage</th>
                    </tr>
                </thead>
                <tbody>
                    {results.length > 0 ? (
                        results.map((item, index) => (
                            <tr key={item.id || `${item.itemName}-${index}`}>
                                <td>{item.itemName}</td>
                                <td>{item.rarity || 'N/A'}</td>
                                <td style={{ minWidth: '50px' }}>{`${item.enemyModDropChance || item.chance} %`}</td>
                                <td>
                                    {
                                        item.tablename === "sortieRewards" ? <span>Sortie</span> :
                                            item.tablename === "transientRewards" ? item.objectiveName :
                                                item.objectiveName ||
                                                    item.source ||
                                                    item.enemyName ? (
                                                    <a href={`https://warframe.fandom.com/wiki/${item.objectiveName || item.source || item.enemyName}`} target="_blank" rel="noopener noreferrer">
                                                        {item.objectiveName || item.source || item.enemyName}
                                                    </a>
                                                ) : item.gameMode ? (
                                                    <span>
                                                        ({item.planet}) {item.location}:
                                                        <br />
                                                        <a href={`https://warframe.fandom.com/wiki/${item.gameMode}`} target="_blank" rel="noopener noreferrer">
                                                            {item.gameMode}
                                                        </a>
                                                    </span>
                                                ) : item.relicName ? (
                                                    <span>
                                                        <a href={`https://warframe.fandom.com/wiki/${item.relicTier}_${item.relicName}`} target="_blank" rel="noopener noreferrer">
                                                            {item.relicTier} {item.relicName}
                                                        </a>
                                                        <span> </span>({item.relicState} {({
                                                            "Intact": "(0 traces)",
                                                            "Exceptional": "(25 traces)",
                                                            "Flawless": "(50 traces)",
                                                            "Radiant": "(100 traces)"
                                                        })[item.relicState] || "Unknown"})
                                                    </span>
                                                ) : (
                                                    item.keyName ||
                                                    item.bountyLevel ||
                                                    `${item.syndicateName} (${item.standing} standing)` ||
                                                    '-'
                                                )
                                    }
                                </td>
                                <td>{item.rotation ? `Rotation ${item.rotation}` : '-'}</td>
                                <td>{item.stage ? `${item.stage}` : '-'}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6">No results found</td>
                        </tr>
                    )}
                </tbody>
            </table>

            {/* Show Load More button if there are more results */}
            {hasMore && !loading && (
                <button onClick={loadMore} style={{ marginTop: '20px' }}>
                    Load More
                </button>
            )}
        </div>
    );
}

export default Search;
