import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ClipLoader } from 'react-spinners';

function Search() {
    const [query, setQuery] = useState('');
    const [rarity, setRarity] = useState('');
    const [results, setResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Sorting state
    const [sortColumn, setSortColumn] = useState(null); // Column to sort by
    const [sortOrder, setSortOrder] = useState('asc'); // Sort order: 'asc' or 'desc'

    const handleSearch = async (event) => {
        if (event) event.preventDefault();
        setLoading(true);
        setError(null);

        try {
            const response = await axios.get('http://localhost:5000/search', {
                params: { query, rarity },
            });
            setResults(response.data);
        } catch (error) {
            console.error('There was an error!', error);
            setError('There was an error with your search. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        handleSearch();
    }, []);

    // Handle sorting
    const handleSort = (column) => {
        if (sortColumn === column) {
            // Toggle sort order if the same column is clicked
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
        } else {
            // Sort by a new column
            setSortColumn(column);
            setSortOrder('asc');
        }
    };

    // Apply sorting to the results
    const sortedResults = [...results].sort((a, b) => {
        if (!sortColumn) return 0;

        // Handle nested results and ensure comparisons are consistent
        const valA = a[sortColumn] || '';
        const valB = b[sortColumn] || '';

        if (valA < valB) return sortOrder === 'asc' ? -1 : 1;
        if (valA > valB) return sortOrder === 'asc' ? 1 : -1;
        return 0;
    });

    return (
        <div>
            <h1>Search for Items</h1>
            <form onSubmit={handleSearch}>
                <div>
                    <input
                        type="text"
                        placeholder="Search for an item by name..."
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        style={{ width: '500px' }}
                        onKeyDownCapture={(e) => {
                            if (e.key === 'Enter') {
                                e.preventDefault();
                                handleSearch(e);
                            }
                        }}
                    />
                    <button type="submit">Search</button>
                </div>
            </form>

            {loading && <ClipLoader size={50} color="#36d7b7" />}

            {error && <p style={{ color: 'red' }}>{error}</p>}

            <h2>Results</h2>
            <table>
                <thead>
                    <tr>
                        <th onClick={() => handleSort('itemName')}>
                            Item Name {sortColumn === 'itemName' && (sortOrder === 'asc' ? '↑' : '↓')}
                        </th>
                        <th onClick={() => handleSort('rarity')}>
                            Rarity {sortColumn === 'rarity' && (sortOrder === 'asc' ? '↑' : '↓')}
                        </th>
                        <th onClick={() => handleSort('chance')}>
                            Chance {sortColumn === 'chance' && (sortOrder === 'asc' ? '↑' : '↓')}
                        </th>
                        <th onClick={() => handleSort('source')}>
                            Source {sortColumn === 'source' && (sortOrder === 'asc' ? '↑' : '↓')}
                        </th>
                        <th>Rotation</th>
                        <th>Stage</th>
                    </tr>
                </thead>
                <tbody>
                    {sortedResults.length > 0 ? (
                        sortedResults.map((itemObj, index) => {
                            if (itemObj.results) {
                                return itemObj.results.map((item) => (
                                    <tr key={item.id || `${item.itemName}-${index}`}>
                                        <td>{item.itemName}</td>
                                        <td>{item.rarity || 'N/A'}</td>
                                        <td style={{ minWidth: '50px' }}>{`${item.enemyModDropChance || item.chance} %`}</td>
                                        <td>{item.source || 'N/A'}</td>
                                        <td>{item.rotation ? `Rotation ${item.rotation}` : '-'}</td>
                                        <td>{item.stage ? `${item.stage}` : '-'}</td>
                                    </tr>
                                ));
                            }
                            return (
                                <tr key={index}>
                                    <td colSpan="4">No items found for this entry</td>
                                </tr>
                            );
                        })
                    ) : (
                        <tr>
                            <td colSpan="4">No results found</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
}

export default Search;
